import { useState } from "react";
import { useHistory } from "react-router-dom";
import  Routingpath  from "../routes/RoutingPath";

export const SigninView = () => {
    const history = useHistory();
    const [loginCredentials, setLoginCredentials] = useState({
    username: '', password: '' });

    const signIn = () => {
        localStorage.setItem('user', loginCredentials.username)
        history.push(Routingpath.HomeView)
    }

    const updateUserNameAndPasswordState = (event) => {
        setLoginCredentials({...loginCredentials, username: event.target.value });
        setLoginCredentials({...loginCredentials, password: event.target.value });

    }

    return (
        <div>
            <h1>{loginCredentials.username}</h1>
            <form>
                <input placeholder="Username" 
                onChange = {updateUserNameAndPasswordState}
                value={loginCredentials.username}/><br/>

                <input placeholder="Password" onChange = {updateUserNameAndPasswordState}/>
                <button onClick = {() => signIn()}>Sign In</button>
            </form>

        </div>
    )
}
